import React, { Component } from 'react';
import { Card } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faList } from '@fortawesome/free-solid-svg-icons';

const tableStyle = {
  color: 'white',
  border: '1px solid white',
};

export default class VoitureListe extends Component {
  render() {
    return (
      <Card className='border border-dark bg-dark text-white mt-4'>
        <Card.Header className="text-white">
          <FontAwesomeIcon icon={faList} /> Liste Voitures
        </Card.Header>
        <Card.Body>
          <table style={tableStyle} className="table table-striped">
            <thead>
              <tr>
                <th style={{ border: '1px solid white' }}>Marque</th>
                <th style={{ border: '1px solid white' }}>Modele</th>
                <th style={{ border: '1px solid white' }}>Couleur</th>
                <th style={{ border: '1px solid white' }}>Annee</th>
                <th style={{ border: '1px solid white' }}>Prix</th>
              </tr>
            </thead>
            <tbody>
              <tr align="center">
                <td colSpan="5">Aucune Voiture nâ€™est disponible</td>
              </tr>
            </tbody>
          </table>
        </Card.Body>
      </Card>
    );
  }
}
