import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";
import { faEdit, faList, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import MyToast from "./MyToast";

class VoitureList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      voitures: [],
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8080/voitures")
      .then((response) => response.data)
      .then((data) => {
        this.setState({ voitures: data });
      });
  }

  deleteVoiture = (voitureId) => {
    axios.delete(`http://localhost:8080/voitures/${voitureId}`)
      .then(response => {
        if (response.data != null) {
          this.setState({ "show": true });
          setTimeout(() => this.setState({ "show": false }), 3000);
          this.setState({
            voitures: this.state.voitures.filter(voiture => voiture.id !== voitureId)
          });
        }
      });
  };

  render() {
    return (
      <div>
        <div style={{ display: this.state.show ? "block" : "none" }}>
          <MyToast
            children={{
              show: this.state.show,
              message: "Voiture supprimée avec succès.",
              type: "danger",
            }}
          />
        </div>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Marque</th>
              <th>Modele</th>
              <th>Couleur</th>
              <th>Immatricule</th>
              <th>Annee</th>
              <th>Prix</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.state.voitures.map((voiture) => (
              <tr key={voiture.id}>
                <td>{voiture.marque}</td>
                <td>{voiture.modele}</td>
                <td>{voiture.couleur}</td>
                <td>{voiture.immatricule}</td>
                <td>{voiture.annee}</td>
                <td>{voiture.prix}</td>
                <td>
                  <Link to={`/list/edit/${voiture.id}`} className="btn btn-sm btn-outline-primary">
                    <FontAwesomeIcon icon={faEdit} />
                  </Link>{' '}
                  <button className="btn btn-sm btn-outline-danger" onClick={() => this.deleteVoiture(voiture.id)}>
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default VoitureList;
