import React, { useState, useEffect } from 'react';
import { Card, Form, Col, Button, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndo, faSave, faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import MyToast from './MyToast';
import { useParams } from 'react-router-dom';

function EditVoiture() {
  const { id } = useParams(); // Retrieve id from route parameters
  const [marque, setMarque] = useState('');
  const [modele, setModele] = useState('');
  const [couleur, setCouleur] = useState('');
  const [immatricule, setImmatricule] = useState('');
  const [annee, setAnnee] = useState('');
  const [prix, setPrix] = useState('');
  const [show, setShow] = useState(false);
  const [toastType, setToastType] = useState('');

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:8080/voitures/${id}`)
        .then((response) => {
          if (response.data) {
            const { marque, modele, couleur, immatricule, annee, prix } = response.data;
            setMarque(marque);
            setModele(modele);
            setCouleur(couleur);
            setImmatricule(immatricule);
            setAnnee(annee);
            setPrix(prix);
          }
        });
    }
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const voiture = {
      marque: marque,
      modele: modele,
      couleur: couleur,
      immatricule: immatricule,
      annee: annee,
      prix: prix,
    };

    axios.put(`http://localhost:8080/voitures/${id}`, voiture)
      .then((response) => {
        if (response.data) {
          setShow(true);
          setToastType('success');
        }
      });
  };

  return (
    <div>
      <Card className={'border border-dark bg-dark text-white'}>
        <Card.Header>
          <FontAwesomeIcon icon={id ? faPlusSquare : faSave} /> {id ? 'Edit Voiture' : 'Add Voiture'}
        </Card.Header>
        <Form onReset={handleSubmit} onSubmit={handleSubmit} id="voitureFormId">
          <Card.Body>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridMarque">
                <Form.Label>Marque</Form.Label>
                <Form.Control required
                  type="text"
                  name="marque"
                  value={marque}
                  onChange={(e) => setMarque(e.target.value)}
                  className="bg-dark text-white"
                  placeholder="Enter Marque" />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridModele">
                <Form.Label>Modele</Form.Label>
                <Form.Control required
                  type="text"
                  name="modele"
                  value={modele}
                  onChange={(e) => setModele(e.target.value)}
                  className="bg-dark text-white"
                  placeholder="Enter Modele" />
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridCouleur">
                <Form.Label>Couleur</Form.Label>
                <Form.Control required
                  type="text"
                  name="couleur"
                  value={couleur}
                  onChange={(e) => setCouleur(e.target.value)}
                  className="bg-dark text-white"
                  placeholder="Enter Couleur" />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridImmatricule">
                <Form.Label>Immatricule</Form.Label>
                <Form.Control required
                  type="text"
                  name="immatricule"
                  value={immatricule}
                  onChange={(e) => setImmatricule(e.target.value)}
                  className="bg-dark text-white"
                  placeholder="Enter Immatricule" />
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridAnnee">
                <Form.Label>Annee</Form.Label>
                <Form.Control required
                  type="text"
                  name="annee"
                  value={annee}
                  onChange={(e) => setAnnee(e.target.value)}
                  className="bg-dark text-white"
                  placeholder="Enter Annee" />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridPrix">
                <Form.Label>Prix</Form.Label>
                <Form.Control required
                  type="text"
                  name="prix"
                  value={prix}
                  onChange={(e) => setPrix(e.target.value)}
                  className="bg-dark text-white"
                  placeholder="Enter Prix" />
              </Form.Group>
            </Form.Row>
          </Card.Body>
          <Card.Footer style={{ "textAlign": "right" }}>
            <Button size="sm" variant="success" type="submit">
              <FontAwesomeIcon icon={faSave} /> {id ? 'Update' : 'Save'}
            </Button>{' '}
            <Button size="sm" variant="info" type="reset">
              <FontAwesomeIcon icon={faUndo} /> Reset
            </Button>
          </Card.Footer>
        </Form>
      </Card>
    </div>
  );
}

export default EditVoiture;
