import React, { Component } from 'react';
import axios from "axios";

export default class EditVoiture extends Component {
  constructor(props) {
    super(props);

    this.state = {
      voiture: {
        marque: '',
        modele: '',
        couleur: '',
        immatricule: '',
        annee: '',
        prix: '',
      },
    };
  }

  componentDidMount() {
    const { match } = this.props;
    const voitureId = match.params.id;

    axios.get(`http://localhost:8080/voitures/${voitureId}`)
      .then((response) => {
        this.setState({ voiture: response.data });
      })
      .catch((error) => {
        console.error(error);
      });
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState((prevState) => ({
      voiture: { ...prevState.voiture, [name]: value },
    }));
  };

  handleUpdate = () => {
    const { voiture } = this.state;
    const { history } = this.props;

    axios.put(`http://localhost:8080/voitures/${voiture.id}`, voiture)
      .then(() => {
        history.push('/list'); // Redirect to the list after update
      })
      .catch((error) => {
        console.error(error);
      });
  };

  render() {
    const { voiture } = this.state;

    return (
      <div>
        <h2>Edit Voiture</h2>
        <div>
          <label>Marque:</label>
          <input
            type="text"
            name="marque"
            value={voiture.marque}
            onChange={this.handleInputChange}
          />
        </div>
        <div>
          <label>Modele:</label>
          <input
            type="text"
            name="modele"
            value={voiture.modele}
            onChange={this.handleInputChange}
          />
        </div>
        <div>
          <label>Couleur:</label>
          <input
            type="text"
            name="couleur"
            value={voiture.couleur}
            onChange={this.handleInputChange}
          />
        </div>
        <div>
          <label>Immatricule:</label>
          <input
            type="text"
            name="immatricule"
            value={voiture.immatricule}
            onChange={this.handleInputChange}
          />
        </div>
        <div>
          <label>Annee:</label>
          <input
            type="text"
            name="annee"
            value={voiture.annee}
            onChange={this.handleInputChange}
          />
        </div>
        <div>
          <label>Prix:</label>
          <input
            type="text"
            name="prix"
            value={voiture.prix}
            onChange={this.handleInputChange}
          />
        </div>
        <button onClick={this.handleUpdate}>Save</button>
      </div>
    );
  }
}
