import React, { Component } from 'react';
import { Card, Form, Col, Button, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndo, faSave, faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios'

export default class Voiture extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState;
        this.voitureChange = this.voitureChange.bind(this);
        this.submitVoiture = this.submitVoiture.bind(this);
    }

    initialState = {
        marque: '',
        modele: '',
        couleur: '',
        immatricule: '',
        annee: '',
        prix: ''
    };

    resetVoiture() {
        this.setState(this.initialState);
    }

    submitVoiture(event) {
        event.preventDefault();
        const voiture = {
            marque: this.state.marque,
            modele: this.state.modele,
            couleur: this.state.couleur,
            immatricule: this.state.immatricule,
            annee: this.state.annee,
            prix: this.state.prix,
        };

        axios.post("http://localhost:8080/voitures", voiture)
            .then(response => {
                if (response.data != null) {
                    this.setState(this.initialState);
                    alert("Voiture enregistrée avec succès");
                }
            });
    }

    voitureChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
        });
    }

    render() {
        const marginTop = {
            marginTop: "20px",
        };
        return (
            <div style={marginTop}>
                <Card className="border border-dark bg-dark text-white">
                    <Card.Header>
                        <FontAwesomeIcon icon={faPlusSquare} />Ajouter Voiture
                    </Card.Header>
                    <Form
                        onReset={this.resetVoiture}
                        onSubmit={this.submitVoiture}
                        id="VoitureFormId"
                    >
                        {/* Rest of your component code */}
                    </Form>
                </Card>
            </div>
        );
    }
}
