import React, { Component, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, Form, Col, Button, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndo, faSave, faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import MyToast from "./MyToast";

const Voiture = () => {
  const { id } = useParams();
  const [voitureData, setVoitureData] = useState({
    marque: '',
    modele: '',
    couleur: '',
    immatricule: '',
    annee: '',
    prix: '',
  });
  const [showToast, setShowToast] = useState(false);
  const [toastType, setToastType] = useState('success');

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:8080/voitures/${id}`)
        .then((response) => {
          setVoitureData(response.data);
        })
        .catch((error) => {
          console.error("Error fetching voiture data: ", error);
        });
    }
  }, [id]);

  const resetVoiture = () => {
    setVoitureData({
      marque: '',
      modele: '',
      couleur: '',
      immatricule: '',
      annee: '',
      prix: '',
    });
  }

  const submitVoiture = (event) => {
    event.preventDefault();
    axios.post("http://localhost:8080/voitures", voitureData)
      .then((response) => {
        if (response.data != null) {
          setShowToast(true);
          setToastType("success");
          resetVoiture();
          setTimeout(() => {
            setShowToast(false);
          }, 3000);
        }
      });
  }

  const voitureChange = (event) => {
    setVoitureData({
      ...voitureData,
      [event.target.name]: event.target.value,
    });
  }

  const marginTop = {
    marginTop: '20px',
  };

  return (
    <div style={marginTop}>
      <div style={{ display: showToast ? "block" : "none" }}>
        <MyToast
          children={{
            show: showToast,
            message: "Voiture enregistrée avec succès",
            type: toastType,
          }}
        />
      </div>
      <Card className="border border-dark bg-dark text-white">
        <Card.Header>
          <FontAwesomeIcon icon={faPlusSquare} /> Ajouter Voiture
        </Card.Header>
        <Form onReset={resetVoiture} onSubmit={submitVoiture} id="VoitureFormId">
          <Card.Body as={Row}>
            <Form.Group as={Col} controlId="formGridMarque">
              <Form.Label>Marque</Form.Label>
              <Form.Control
                required
                type="text"
                name="marque"
                value={voitureData.marque}
                autoComplete="off"
                onChange={voitureChange}
                className="bg-dark text-white"
                placeholder="Entrez Marque Voiture"
              />
            </Form.Group>
            {/* Rest of the form fields */}
          </Card.Body>
          <Card.Footer style={{ textAlign: 'right' }}>
            <Button size="sm" variant="success" type="submit">
              <FontAwesomeIcon icon={faSave} /> Submit
            </Button>{' '}
            <Button size="sm" variant="info" type="reset">
              <FontAwesomeIcon icon={faUndo} /> Reset
            </Button>
          </Card.Footer>
        </Form>
      </Card>
    </div>
  );
}

export default Voiture;
