import React, { Component } from 'react';
import { Card, Form, Col, Button, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndo, faSave, faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import MyToast from './MyToast';

export default class EditVoiture extends Component {
  constructor(props) {
    super(props);
    this.state = {
      marque: '',
      modele: '',
      couleur: '',
      immatricule: '',
      annee: '',
      prix: '',
      show: false,
      toastType: '',
    };
    this.voitureChange = this.voitureChange.bind(this);
    this.submitVoiture = this.submitVoiture.bind(this);
  }

    componentDidMount() {
      // Fetch the voiture data based on the ID from the URL
      const { id } = this.props.match ? this.props.match.params : { id: '' };
      if (id) {
        axios.get('http://localhost:8080/voitures/' + id)
          .then((response) => {
            const voitureData = response.data;
            
            this.setState({
              marque: voitureData.marque,
              modele: voitureData.modele,
              couleur: voitureData.couleur,
              immatricule: voitureData.immatricule,
              annee: voitureData.annee,
              prix: voitureData.prix,
            });
          })
          .catch((error) => {
            console.error('Error fetching voiture data: ', error);
          });
      }
    }


  resetVoiture() {
    this.setState({
      marque: '',
      modele: '',
      couleur: '',
      immatricule: '',
      annee: '',
      prix: '',
      show: false,
      toastType: '',
    });
  }

  submitVoiture(event) {
    event.preventDefault();
    const { id } = this.props.match.params;
    const voiture = {
      marque: this.state.marque,
      modele: this.state.modele,
      couleur: this.state.couleur,
      immatricule: this.state.immatricule,
      annee: this.state.annee,
      prix: this.state.prix,
    };

    if (id) {
      // If id is present, update an existing voiture
      axios.put('http://localhost:8080/voitures/' + id, voiture)
        .then((response) => {
          if (response.data != null) {
            this.setState({
              ...this.initialState,
              show: true,
              toastType: 'success',
            });
            setTimeout(() => {
              this.setState({ show: false });
            }, 3000);
          }
        });
    }
  }

  voitureChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  render() {
    const marginTop = {
      marginTop: '20px',
    };

    return (
      <div style={marginTop}>
        <div style={{ display: this.state.show ? 'block' : 'none' }}>
          <MyToast
            children={{
              show: this.state.show,
              message: 'Voiture mise à jour avec succès',
              type: 'success',
            }}
          />
        </div>
        <Card className="border borderDark bg-dark text-white">
          <Card.Header>
            <FontAwesomeIcon icon={faPlusSquare} /> Modifier Voiture
          </Card.Header>
          <Form onReset={this.resetVoiture} onSubmit={this.submitVoiture} id="VoitureFormId">
            <Card.Body as={Row}>
              <Form.Group as={Col} controlId="formGridMarque">
                <Form.Label>Marque</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="marque"
                  value={this.state.marque}
                  autoComplete="off"
                  onChange={this.voitureChange}
                  className="bg-dark text-white"
                  placeholder="Entrez Marque Voiture"
                />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridModele">
                <Form.Label>Modèle</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="modele"
                  value={this.state.modele}
                  autoComplete="off"
                  onChange={this.voitureChange}
                  className="bg-dark text-white"
                  placeholder="Entrez Modèle de Voiture"
                />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridCouleur">
                <Form.Label>Couleur</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="couleur"
                  value={this.state.couleur}
                  autoComplete="off"
                  onChange={this.voitureChange}
                  className="bg-dark text-white"
                  placeholder="Entrez Couleur de Voiture"
                />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridImmatricule">
                <Form.Label>Immatricule</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="immatricule"
                  value={this.state.immatricule}
                  autoComplete="off"
                  onChange={this.voitureChange}
                  className="bg-dark text-white"
                  placeholder="Entrez Immatricule de Voiture"
                />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridAnnee">
                <Form.Label>Annee</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="annee"
                  value={this.state.annee}
                  autoComplete="off"
                  onChange={this.voitureChange}
                  className="bg-dark text-white"
                  placeholder="Entrez Année de Voiture"
                />
              </Form.Group>
              <Form.Group as={Col} controlId="formGridPrix">
                <Form.Label>Prix</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="prix"
                  value={this.state.prix}
                  autoComplete="off"
                  onChange={this.voitureChange}
                  className="bg-dark text-white"
                  placeholder="Entrez Prix de Voiture"
                />
              </Form.Group>
            </Card.Body>
            <Card.Footer style={{ textAlign: 'right' }}>
              <Button size="sm" variant="success" type="submit">
                <FontAwesomeIcon icon={faSave} /> Modifier
              </Button>{' '}
              <Button size="sm" variant="info" type="reset">
                <FontAwesomeIcon icon={faUndo} /> Reset
              </Button>
            </Card.Footer>
          </Form>
        </Card>
      </div>
    );
  }
}
